import { createAction, props } from '@ngrx/store';

export interface Coupon {
  code: string;
  discount: number;
  type: 'percentage' | 'fixed';
  minAmount?: number;
  maxUses?: number;
  expiresAt?: string;
}

export const applyCoupon = createAction('[Discounts] Apply Coupon', props<{ code: string }>());

export const applyCouponSuccess = createAction(
  '[Discounts] Apply Coupon Success',
  props<{ coupon: Coupon; discountAmount: number }>(),
);

export const applyCouponFailure = createAction(
  '[Discounts] Apply Coupon Failure',
  props<{ error: string }>(),
);

export const removeCoupon = createAction('[Discounts] Remove Coupon');

export const addProductDiscount = createAction(
  '[Discounts] Add Product Discount',
  props<{ productId: number; discount: number }>(),
);

export const removeProductDiscount = createAction(
  '[Discounts] Remove Product Discount',
  props<{ productId: number }>(),
);

import { createAction, props } from '@ngrx/store';

export interface DeliveryOption {
  id: string;
  name: string;
  cost: number;
  estimatedDays: number;
  description: string;
}

export const selectDeliveryOption = createAction(
  '[Delivery] Select Option',
  props<{ option: DeliveryOption }>(),
);

export const setDeliveryOptions = createAction(
  '[Delivery] Set Options',
  props<{ options: DeliveryOption[] }>(),
);

export const setFreeShipping = createAction('[Delivery] Set Free Shipping');

export const clearFreeShipping = createAction('[Delivery] Clear Free Shipping');

import { createReducer, on } from '@ngrx/store';
import * as DeliveryActions from './delivery.actions';
import { DeliveryOption } from './delivery.actions';

export interface DeliveryState {
  options: DeliveryOption[];
  selectedOption: DeliveryOption | null;
  isFreeShippingActive: boolean;
}

const initialDeliveryOptions: DeliveryOption[] = [
  {
    id: 'standard',
    name: 'Standard Delivery',
    cost: 5.99,
    estimatedDays: 5,
    description: 'Delivery in 5-7 business days',
  },
  {
    id: 'express',
    name: 'Express Delivery',
    cost: 12.99,
    estimatedDays: 2,
    description: 'Delivery in 2-3 business days',
  },
  {
    id: 'overnight',
    name: 'Overnight Delivery',
    cost: 24.99,
    estimatedDays: 1,
    description: 'Next business day delivery',
  },
  {
    id: 'pickup',
    name: 'Store Pickup',
    cost: 0,
    estimatedDays: 0,
    description: 'Free pickup at store',
  },
];

const initialState: DeliveryState = {
  options: initialDeliveryOptions,
  selectedOption: initialDeliveryOptions[0],
  isFreeShippingActive: false,
};

export const deliveryReducer = createReducer(
  initialState,

  on(DeliveryActions.selectDeliveryOption, (state, { option }) => ({
    ...state,
    selectedOption: option,
  })),

  on(DeliveryActions.setDeliveryOptions, (state, { options }) => ({
    ...state,
    options,
    selectedOption: state.selectedOption || options[0],
  })),

  on(DeliveryActions.setFreeShipping, (state) => ({
    ...state,
    isFreeShippingActive: true,
  })),

  on(DeliveryActions.clearFreeShipping, (state) => ({
    ...state,
    isFreeShippingActive: false,
  })),
);

import { createReducer, on } from '@ngrx/store';
import * as DiscountsActions from './discounts.actions';
import { Coupon } from './discounts.actions';

export interface DiscountsState {
  appliedCoupon: Coupon | null;
  discountAmount: number;
  error: string | null;
  productDiscounts: { [productId: number]: number };
}

const initialState: DiscountsState = {
  appliedCoupon: null,
  discountAmount: 0,
  error: null,
  productDiscounts: {},
};

export const discountsReducer = createReducer(
  initialState,

  on(DiscountsActions.applyCoupon, (state, { code }) => state),

  on(DiscountsActions.applyCouponSuccess, (state, { coupon, discountAmount }) => ({
    ...state,
    appliedCoupon: coupon,
    discountAmount,
    error: null,
  })),

  on(DiscountsActions.applyCouponFailure, (state, { error }) => ({
    ...state,
    error,
    appliedCoupon: null,
    discountAmount: 0,
  })),

  on(DiscountsActions.removeCoupon, (state) => ({
    ...state,
    appliedCoupon: null,
    discountAmount: 0,
    error: null,
  })),

  on(DiscountsActions.addProductDiscount, (state, { productId, discount }) => ({
    ...state,
    productDiscounts: {
      ...state.productDiscounts,
      [productId]: discount,
    },
  })),

  on(DiscountsActions.removeProductDiscount, (state, { productId }) => {
    const newProductDiscounts = { ...state.productDiscounts };
    delete newProductDiscounts[productId];
    return {
      ...state,
      productDiscounts: newProductDiscounts,
    };
  }),
);

