  updatedAt?: string;
}

export interface OrderItem {
  productId: number;
  productName: string;
  quantity: number;
  price: number;
  discount?: number;
}

export interface OrderSummary {
  id: string;
  orderDate: string;
  status: 'pending' | 'processing' | 'shipped' | 'delivered' | 'cancelled';
  totalPrice: number;
  itemCount: number;
  subtotalPrice?: number;
  shippingCost?: number;
  taxAmount?: number;
}

export interface OrderDetails extends OrderSummary {
  items: OrderItem[];
  shippingAddress: Address;
  billingAddress: Address;
  trackingNumber?: string;
  estimatedDelivery?: string;
}

export interface UserProfileResponse {
  user: User;
}

export interface UserResponse extends User {}

export interface OrdersListResponse {
  count: number;
  results: OrderSummary[];
}

import { createReducer, on } from '@ngrx/store';
import * as UserActions from './user.actions';
import { User, OrderDetails } from '../../services/types';

export interface UserState {
  user: User | null;
  selectedOrder: OrderDetails | null;
  loading: boolean;
  error: string | null;
}

export const initialUserState: UserState = {
  user: null,
  selectedOrder: null,
  loading: false,
  error: null,
};

export const userReducer = createReducer(
  initialUserState,

  on(UserActions.loadUserProfile, (state) => ({
    ...state,
    loading: true,
    error: null,
  })),

  on(UserActions.loadUserProfileSuccess, (state, { user }) => ({
    ...state,
    user,
    loading: false,
    error: null,
  })),

  on(UserActions.loadUserProfileFailure, (state, { error }) => ({
    ...state,
    loading: false,
    error,
  })),

  on(UserActions.updateUserProfile, (state) => ({
    ...state,
    loading: true,
    error: null,
  })),

  on(UserActions.updateUserProfileSuccess, (state, { user }) => ({
    ...state,
    user,
    loading: false,
    error: null,
  })),

  on(UserActions.updateUserProfileFailure, (state, { error }) => ({
    ...state,
    loading: false,
    error,
  })),

  on(UserActions.loadOrders, (state) => ({
    ...state,
    loading: true,
    error: null,
  })),

  on(UserActions.loadOrdersSuccess, (state, { orders }) => ({
    ...state,
    user: state.user ? { ...state.user, orders } : state.user,
    loading: false,
    error: null,
  })),

  on(UserActions.loadOrdersFailure, (state, { error }) => ({
    ...state,
    loading: false,
    error,
  })),

  on(UserActions.loadOrderDetails, (state) => ({
    ...state,
    loading: true,
    error: null,
  })),

  on(UserActions.loadOrderDetailsSuccess, (state, { order }) => ({
    ...state,
    selectedOrder: order,
    loading: false,
    error: null,
  })),

  on(UserActions.loadOrderDetailsFailure, (state, { error }) => ({
    ...state,
    loading: false,
    error,
  })),

  on(UserActions.clearOrderDetails, (state) => ({
    ...state,
    selectedOrder: null,
  })),

  on(UserActions.logoutUser, () => initialUserState),
);

import { createAction, props } from '@ngrx/store';
import { Product } from '../../services/types';

export interface WishlistItem {
  id: number;
  name: string;
  price: number;
  image: string;
  stock?: number;
  lowStockThreshold?: number;
  created_at?: string;
  avgRating?: number;
  discount?: number;
}

export const addToWishlist = createAction(
  '[Wishlist] Add To Wishlist',
  props<{ product: WishlistItem }>(),
);

export const removeFromWishlist = createAction(
  '[Wishlist] Remove From Wishlist',
  props<{ productId: number }>(),
);

export const clearWishlist = createAction('[Wishlist] Clear Wishlist');

export const loadWishlistFromStorage = createAction(
  '[Wishlist] Load From Storage',
  props<{ productIds: number[] }>(),
);

import { createReducer, on } from '@ngrx/store';
import * as WishlistActions from './wishlist.actions';
import { WishlistItem } from './wishlist.actions';

export interface WishlistState {
  items: WishlistItem[];
}

const initialState: WishlistState = {
  items: [],
};

export const wishlistReducer = createReducer(
  initialState,

  on(WishlistActions.addToWishlist, (state, { product }) => ({
    ...state,
    items: state.items.some((item) => item.id === product.id)
      ? state.items
      : [...state.items, product],
  })),

  on(WishlistActions.removeFromWishlist, (state, { productId }) => ({
    ...state,
    items: state.items.filter((item) => item.id !== productId),
  })),

  on(WishlistActions.clearWishlist, (state) => ({
    ...state,
    items: [],
  })),

  on(
    WishlistActions.loadWishlistFromStorage,
    (state, { productIds }) =>
      // Note: This action should load full product data from store, not just IDs
      // For now, keep existing items if loading from storage
      state,
  ),
);

