import { Component, signal, inject, OnInit } from '@angular/core';
import { RouterOutlet } from '@angular/router';
import { Store } from '@ngrx/store';
import { HeaderComponent } from './components/header.component';
import { appInit } from './state/app.actions';
import { restoreAuthFromStorage } from './state/auth/auth.actions';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [RouterOutlet, HeaderComponent],
  templateUrl: './app.html',
  styleUrl: './app.css',
})
export class App implements OnInit {
  protected readonly title = signal('my-shop');
  private readonly store = inject(Store);

  ngOnInit() {
    this.store.dispatch(restoreAuthFromStorage());
    this.store.dispatch(appInit());
  }
}

styles:
.app-shell {
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  background: linear-gradient(135deg, #f0f9ff 0%, #e0f2fe 50%, #e0e7ff 100%);
}

.content {
  flex: 1;
  padding: 32px;
  max-width: 1100px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  gap: 32px;
}

.hero {
  padding: 24px 28px;
  border-radius: 20px;
  background: linear-gradient(135deg, #0284c7 0%, #0369a1 100%);
  color: #ffffff !important;
  box-shadow: 0 18px 40px rgba(2, 132, 199, 0.35);
}

.hero-inner {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 24px;
}

.hero-text h1 {
  margin: 0 0 8px;
  font-size: 28px;
  font-weight: 600;
}

.hero-text p {
  margin: 0 0 0;
  font-size: 14px;
  opacity: 0.95;
}

.hero-actions {
  display: flex;
  flex-wrap: wrap;
  gap: 12px;
}

.features-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(260px, 1fr));
  gap: 20px;
}

.feature-card {
  border-radius: 18px;
  padding: 18px 18px 20px;
  border: 1px solid #e3e6ea;
  background: linear-gradient(145deg, #ffffff, #f7f9fc);
  display: flex;
  flex-direction: column;
  gap: 10px;
  box-shadow: 0 10px 24px rgba(15, 23, 42, 0.06);
  transition:
