import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterLink } from '@angular/router';
import { Store } from '@ngrx/store';
import { selectIsAuthenticated } from './state/auth/auth.selectors';
import * as AuthActions from './state/auth/auth.actions';
import { MatButtonModule } from '@angular/material/button';
import { MatCardModule } from '@angular/material/card';
import { MatIconModule } from '@angular/material/icon';
import { Observable } from 'rxjs';

@Component({
  standalone: true,
  selector: 'app-placeholder',
  imports: [CommonModule, RouterLink, MatButtonModule, MatCardModule, MatIconModule],
  template: `
    <div class="app-shell">
      <main class="content">
        <section class="hero">
          <div class="hero-inner">
            <div class="hero-text">
              <h1 class="!text-white">Welcome to My Shop</h1>
              <p class="!text-white">
                Browse a compact shop demo with sign-in, product exploration, and rating features.
              </p>
            </div>
          </div>
        </section>

        <section class="features-grid">
          <mat-card class="feature-card">
            <mat-icon class="feature-icon">person</mat-icon>
            <h3>Mon profil</h3>
            <p>Voir et mettre  jour vos informations personnelles.</p>
            <button
              mat-stroked-button
              routerLink="/account/profile"
              class="!border-sky-500 !text-sky-600 hover:!bg-sky-50"
            >
              Voir le profil
            </button>
          </mat-card>

          <mat-card class="feature-card">
            <mat-icon class="feature-icon">receipt_long</mat-icon>
            <h3>Commandes rcentes</h3>
            <p>Retrouvez vos dernires commandes sans quitter cette page.</p>
            <button
              mat-stroked-button
              routerLink="/account/orders"
              class="!border-sky-500 !text-sky-600 hover:!bg-sky-50"
            >
              Voir mes commandes
            </button>
          </mat-card>
          <mat-card class="feature-card">
            <mat-icon class="feature-icon">login</mat-icon>
            <h3>Sign In</h3>
            <p>Access your account with the demo credentials <code>demo/demo</code>.</p>
            <button
              mat-stroked-button
              routerLink="/login"
              class="!border-sky-500 !text-sky-600 hover:!bg-sky-50"
            >
              Go to Login
            </button>
          </mat-card>

          <mat-card class="feature-card">
            <mat-icon class="feature-icon">shopping_bag</mat-icon>
            <h3>Shop Products</h3>
            <p>Browse the product list with filters and sorting options.</p>
            <button
              mat-stroked-button
              routerLink="/shop/products"
              class="!border-sky-500 !text-sky-600 hover:!bg-sky-50"
            >
              Browse products
            </button>
          </mat-card>

          <mat-card class="feature-card">
            <mat-icon class="feature-icon">star_rate</mat-icon>
            <h3>Check Ratings</h3>
            <p>See aggregate ratings for any product by its ID.</p>
            <button
              mat-stroked-button
              routerLink="/shop/rating"
              class="!border-sky-500 !text-sky-600 hover:!bg-sky-50"
            >
              View Ratings
            </button>
          </mat-card>

          <mat-card class="feature-card">
            <mat-icon class="feature-icon">engineering</mat-icon>
            <h3>Dev Testing</h3>
            <p>Access the development and testing zone for component exploration.</p>
            <button
              mat-stroked-button
              routerLink="/dev"
              class="!border-sky-500 !text-sky-600 hover:!bg-sky-50"
            >
              Go to Dev
            </button>
          </mat-card>
        </section>
      </main>
    </div>
  `,
  styles: [
    `
      .app-shell {
        min-height: 100vh;
        display: flex;
        flex-direction: column;
        background: linear-gradient(135deg, #f0f9ff 0%, #e0f2fe 50%, #e0e7ff 100%);
      }

      .content {
        flex: 1;
        padding: 32px;
        max-width: 1100px;
        margin: 0 auto;
        display: flex;
        flex-direction: column;
        gap: 32px;
      }

      .hero {
        padding: 24px 28px;
        border-radius: 20px;
        background: linear-gradient(135deg, #0284c7 0%, #0369a1 100%);
        color: #ffffff !important;
        box-shadow: 0 18px 40px rgba(2, 132, 199, 0.35);
      }

      .hero-inner {
        display: flex;
        align-items: center;
        justify-content: space-between;
        gap: 24px;
      }

      .hero-text h1 {
        margin: 0 0 8px;
        font-size: 28px;
        font-weight: 600;
      }

      .hero-text p {
        margin: 0 0 0;
        font-size: 14px;
        opacity: 0.95;
      }

      .hero-actions {
        display: flex;
        flex-wrap: wrap;
        gap: 12px;
      }

      .features-grid {
        display: grid;
        grid-template-columns: repeat(auto-fit, minmax(260px, 1fr));
        gap: 20px;
      }

      .feature-card {
        border-radius: 18px;
        padding: 18px 18px 20px;
        border: 1px solid #e3e6ea;
        background: linear-gradient(145deg, #ffffff, #f7f9fc);
        display: flex;
        flex-direction: column;
        gap: 10px;
        box-shadow: 0 10px 24px rgba(15, 23, 42, 0.06);
        transition:
          transform 0.18s ease,
          box-shadow 0.18s ease,
          border-color 0.18s ease;
      }

      .feature-card:hover {
        transform: translateY(-4px);
        box-shadow: 0 16px 30px rgba(15, 23, 42, 0.09);
        border-color: #c5d0de;
      }

      .feature-icon {
        font-size: 26px;
        color: #0284c7;
      }

      .feature-card h3 {
        margin: 0;
        font-size: 16px;
        font-weight: 600;
        color: #202124;
      }

      .feature-card p {
        margin: 0;
        font-size: 14px;
        color: #5f6368;
      }

      .feature-card button {
        margin-top: 6px;
        text-transform: none;
      }

      @media (max-width: 768px) {
        .top-nav {
          padding: 10px 16px;
          flex-wrap: wrap;
        }

        .nav-links {
          order: 3;
          justify-content: flex-start;
          flex-wrap: wrap;
        }

        .content {
          padding: 16px;
        }

        .hero-inner {
          flex-direction: column;
          align-items: flex-start;
        }
      }
    `,
  ],
})
export class AppPlaceholderComponent {
  isAuthenticated$: Observable<boolean>;

  constructor(private store: Store) {
    this.isAuthenticated$ = this.store.select(selectIsAuthenticated);
  }

  logout(): void {
    this.store.dispatch(AuthActions.logout());
  }
}

