  font-weight: 600;
}

.discount-badge {
  margin: 0;
  color: #dc2626;
  font-size: 12px;
  font-weight: 700;
  background-color: #fee2e2;
  padding: 2px 6px;
  border-radius: 4px;
}

.product-stock {
  margin: 6px 0 0 0;
  font-size: 13px;
  font-weight: 500;
  display: flex;
  align-items: center;
  gap: 4px;
}

.stock-icon {
  font-size: 16px;
  width: 16px;
  height: 16px;
}

.quantity-control {
  display: flex;
  align-items: center;
  gap: 8px;
}

.qty-label {
  font-weight: 500;
  font-size: 14px;
  color: #555;
}

.qty-input {
  width: 60px;
  padding: 6px 8px;
  border: 1px solid #ccc;
  border-radius: 4px;
  text-align: center;
  font-size: 14px;
}

.item-total {
  text-align: right;
  min-width: 100px;
}

.total-label {
  margin: 0;
  color: #666;
  font-size: 12px;
  font-weight: 500;
}

.total-value {
  margin: 4px 0 0 0;
  font-weight: 600;
  color: #1976d2;
  font-size: 16px;
}

.remove-btn {
  margin-left: auto;
  transition: all 0.2s ease;
}

.remove-btn:hover {
  color: #dc2626 !important;
  transform: scale(1.1);
}

@keyframes slideIn {
  from {
    opacity: 0;
    transform: translateY(10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes quantityPulse {
  0%,
  100% {
    background-color: white;
  }
  50% {
    background-color: #fef3c7;
  }
}

.cart-item-card {
  animation: slideIn 0.3s ease-out;
}

.qty-input {
  transition: background-color 0.2s ease;
}

.qty-input:focus {
  background-color: #fef3c7;
}

@media (max-width: 640px) {
  .item-content {
    flex-direction: column;
    align-items: flex-start;
  }

  .item-total,
  .remove-btn {
    align-self: flex-end;
    margin-left: 0;
  }
}

