  margin-bottom: 0;
}

.apply-button {
  margin-top: 8px !important;
  height: 46px !important;
}

.coupon-applied {
  font-size: 12px;
  font-weight: 500;
  padding: 4px 0;
}

.coupon-error {
  font-size: 12px;
  font-weight: 500;
  padding: 4px 0;
}

.remove-coupon-btn {
  color: #dc2626;
  border-color: #dc2626;
  font-size: 12px;
  height: auto;
  padding: 4px 12px;
}

.remove-coupon-btn:hover {
