      { productId: '3', name: 'Phone Case', sold: 412, revenue: 2472 },
      { productId: '4', name: 'Screen Protector', sold: 328, revenue: 1640 },
      { productId: '5', name: 'USB-C Cable', sold: 567, revenue: 2835 },
    ];

    // Recent orders
    const recentOrders = dynamicOrders.slice(0, 5).map((order) => ({
      id: order.order_number,
      user: 'demo@example.com',
      total: order.total_amount || 0,
      createdAt: order.created_at,
      status: order.status,
    }));

    return HttpResponse.json(
      {
        totalUsers,
        totalOrders,
        totalRevenue,
        topProducts,
        recentOrders,
      },
      { status: 200 },
    );
  }),
]

src/app/guards/auth.guard.ts.defaultValue:
(
  route: ActivatedRouteSnapshot,
  state: RouterStateSnapshot,
) => {
  const store = inject(Store);
  const router = inject(Router);
  return store.select(selectIsAuthenticated).pipe(
    take(1),
    map((isAuthenticated) => {
      if (isAuthenticated) {
        return true;
      }
      router.navigate(['/login'], {
        queryParams: { returnUrl: state.url },
      });
      return false;
    }),
  );
}

src/app/services/auth.interceptor.ts.defaultValue:
(req, next) => {
  const store = inject(Store);

  return store.select(selectAccessToken).pipe(
    take(1),
    switchMap((token) => {
      let authReq = req;

      if (token) {
        authReq = req.clone({
          setHeaders: {
            Authorization: `Bearer ${token}`,
          },
        });
        console.log(`[Interceptor] Added Authorization header to ${req.url}`);
      }

      return next(authReq);
    }),
  );
}

