src/app/state/reviews/reviews.selectors..defaultValue:
(productId: number) =>
  createSelector(selectReviewsByProductId(productId), (reviews) => {
    if (reviews.length === 0) return 0;
    const sum = reviews.reduce((acc, review) => acc + review.rating, 0);
    return Number((sum / reviews.length).toFixed(1));
  })

(productId: number, minRating?: number) =>
  createSelector(selectReviewsByProductId(productId), (reviews) => {
    if (!minRating) return reviews;
    return reviews.filter((review) => review.rating >= minRating);
  })

(productId: number, sortBy: 'newest' | 'highest' = 'newest') =>
  createSelector(selectReviewsByProductId(productId), (reviews) => {
    const sorted = [...reviews];
    if (sortBy === 'newest') {
      return sorted.sort(
        (a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime(),
      );
    } else if (sortBy === 'highest') {
      return sorted.sort((a, b) => b.rating - a.rating);
    }
    return sorted;
  })

