src/app/state/user/user.selectors.ts.defaultValue:
(filters?: {
  status?: string;
  minPrice?: number;
  maxPrice?: number;
  searchTerm?: string;
}) =>
  createSelector(selectOrders, (orders) => {
    if (!filters) return orders;

    return orders.filter((order) => {
      // Filter by status
      if (filters.status && order.status !== filters.status) return false;

      // Filter by price range
      if (filters.minPrice !== undefined && order.totalPrice < filters.minPrice) return false;
      if (filters.maxPrice !== undefined && order.totalPrice > filters.maxPrice) return false;

      // Filter by search term (search in order ID)
      if (filters.searchTerm && !order.id.toLowerCase().includes(filters.searchTerm.toLowerCase()))
        return false;

      return true;
    });
  })

createSelector(selectOrders, (orders) => {
  const now = new Date();
  const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());
  const weekAgo = new Date(today.getTime() - 7 * 24 * 60 * 60 * 1000);
  const monthAgo = new Date(today.getFullYear(), today.getMonth() - 1, today.getDate());

  return {
    today: orders.filter((o) => new Date(o.orderDate) >= today),
    thisWeek: orders.filter((o) => new Date(o.orderDate) >= weekAgo),
    thisMonth: orders.filter((o) => new Date(o.orderDate) >= monthAgo),
    older: orders.filter((o) => new Date(o.orderDate) < monthAgo),
    recentCount: 5,
    topOrder: orders.reduce(
      (max, order) => (order.totalPrice > max.totalPrice ? order : max),
      orders[0] || ({ totalPrice: 0 } as any),
    ),
  };
})

createSelector(selectOrders, (orders) => {
  const total = orders.length;
  const totalSpent = orders.reduce((sum, order) => sum + order.totalPrice, 0);
  const averageOrderValue = total > 0 ? totalSpent / total : 0;

  const byStatus = {
    pending: orders.filter((o) => o.status === 'pending').length,
    processing: orders.filter((o) => o.status === 'processing').length,
    shipped: orders.filter((o) => o.status === 'shipped').length,
    delivered: orders.filter((o) => o.status === 'delivered').length,
    cancelled: orders.filter((o) => o.status === 'cancelled').length,
  };

  return {
    total,
    totalSpent,
    averageOrderValue,
    byStatus,
    completedOrders: byStatus.delivered,
    activeOrders: byStatus.pending + byStatus.processing + byStatus.shipped,
  };
})

src/app/state/wishlist/wishlist.selector.defaultValue:
createSelector(selectWishlistItems, (items) => {
  const totalValue = items.reduce((sum, item) => sum + item.price, 0);

  const averagePrice = items.length > 0 ? totalValue / items.length : 0;
  const maxPrice = items.length > 0 ? Math.max(...items.map((i) => i.price)) : 0;
  const minPrice = items.length > 0 ? Math.min(...items.map((i) => i.price)) : 0;

  return {
    items,
    totalValue,
    averagePrice,
    maxPrice,
    minPrice,
    hasItems: items.length > 0,
    count: items.length,
  };
})

createSelector(selectWishlistItems, (items) => {
  const sorted = [...items].sort((a, b) => a.price - b.price);
  const highPriceItems = items.filter((item) => item.price > 100);
  const mediumPriceItems = items.filter((item) => item.price >= 50 && item.price <= 100);
  const lowPriceItems = items.filter((item) => item.price < 50);

  return {
    highPrice: highPriceItems,
    mediumPrice: mediumPriceItems,
    lowPrice: lowPriceItems,
    mostExpensive: sorted[sorted.length - 1],
    cheapest: sorted[0],
    totalValue: items.reduce((sum, item) => sum + item.price, 0),
  };
})

createSelector(selectWishlistItems, (items) => ({
  available: items.filter((item) => (item.stock || 0) > 0),
  unavailable: items.filter((item) => (item.stock || 0) === 0),
  averageStock:
    items.length > 0 ? items.reduce((sum, item) => sum + (item.stock || 0), 0) / items.length : 0,
}))

createSelector(selectWishlistItems, (items) => ({
