  '[Products] Load Products',
  props<{ filters?: ProductsFilters }>()
);

export const loadProductsSuccess = createAction(
  '[Products] Load Products Success',
  props<{ data: { count: number; results: Product[] } }>()
);

export const loadProductsFailure = createAction(
  '[Products] Load Products Failure',
  props<{ error: string }>()
);

export const loadRating = createAction(
  '[Products] Load Rating',
  props<{ productId: number }>()
);

export const loadRatingSuccess = createAction(
  '[Products] Load Rating Success',
  props<{ data: { product_id: number; avg_rating: number; count: number } }>()
);

export const loadRatingFailure = createAction(
  '[Products] Load Rating Failure',
  props<{ error: string }>()
);

export interface AuthTokenResponse {
  access: string;
  refresh: string;
}

export interface AuthRefreshResponse {
  access: string;
}

export interface Product {
  id: number;
  name: string;
  price: number;
  created_at: string;
  avgRating: number;
}

export interface ProductsListResponse {
  count: number;
  results: Product[];
}

export interface ProductRatingResponse {
  product_id: number;
  avg_rating: number;
  count: number;
}

export interface ApiError {
  message: string;
  status?: number;
}

import { createReducer, on } from '@ngrx/store';
import * as ProductsActions from './products.actions';
import { Product, ProductsFilters } from './products.actions';

export interface ProductsState {
  items: Product[];
  count: number;
  loading: boolean;
  error: string | null;
  lastFilters: ProductsFilters | null;
  rating: {
    product_id: number;
    avg_rating: number;
    count: number;
  } | null;
  ratingLoading: boolean;
  ratingError: string | null;
}

const initialState: ProductsState = {
  items: [],
  count: 0,
  loading: false,
  error: null,
  lastFilters: null,
  rating: null,
  ratingLoading: false,
  ratingError: null,
};

export const productsReducer = createReducer(
  initialState,

  // Load Products
  on(ProductsActions.loadProducts, (state, { filters }) => ({
    ...state,
    loading: true,
    error: null,
    lastFilters: filters || null,
  })),

  on(ProductsActions.loadProductsSuccess, (state, { data }) => ({
    ...state,
    items: data.results,
    count: data.count,
    loading: false,
    error: null,
  })),

  on(ProductsActions.loadProductsFailure, (state, { error }) => ({
    ...state,
    loading: false,
    error,
  })),

  // Load Rating
  on(ProductsActions.loadRating, (state) => ({
    ...state,
    ratingLoading: true,
    ratingError: null,
  })),

  on(ProductsActions.loadRatingSuccess, (state, { data }) => ({
    ...state,
    rating: data,
    ratingLoading: false,
    ratingError: null,
  })),

  on(ProductsActions.loadRatingFailure, (state, { error }) => ({
    ...state,
    ratingLoading: false,
    ratingError: error,
  }))
);

