import { Component, Input, Output, EventEmitter } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';
import { MatInputModule } from '@angular/material/input';
import { MatCardModule } from '@angular/material/card';
import { MatTooltipModule } from '@angular/material/tooltip';
import { CartItem } from '../../state/cart/cart.models';

@Component({
  standalone: true,
  selector: 'app-cart-item',
  imports: [
    CommonModule,
    FormsModule,
    MatButtonModule,
    MatIconModule,
    MatInputModule,
    MatCardModule,
    MatTooltipModule,
  ],
  template: `
    <mat-card class="cart-item-card">
      <div class="item-content">
        <div class="product-info">
          <h3 class="product-name">{{ item.product.name }}</h3>
          <p class="product-price">{{ formatPrice(item.product.price) }}</p>
          <p
            class="product-stock"
            [ngClass]="{
              'text-green-600': item.product.stock > 20,
              'text-yellow-600': item.product.stock > 5 && item.product.stock <= 20,
              'text-red-600': item.product.stock <= 5,
            }"
          >
            <mat-icon class="stock-icon">{{
              item.product.stock > 0 ? 'check_circle' : 'error'
            }}</mat-icon>
            {{ item.product.stock }} in stock
          </p>
        </div>

        <div class="quantity-control">
          <label [for]="'qty-' + item.product.id" class="qty-label">Qty:</label>
          <input
            [id]="'qty-' + item.product.id"
            type="number"
            min="1"
            [(ngModel)]="quantity"
            (change)="onQuantityChange()"
            class="qty-input"
          />
        </div>

        <div class="item-total">
          <p class="total-label">Subtotal:</p>
          <p class="total-value">{{ formatPrice(itemTotal) }}</p>
        </div>

        <button
          mat-icon-button
          color="warn"
          (click)="onRemove()"
          class="remove-btn"
          matTooltip="Remove from cart"
        >
          <mat-icon>delete</mat-icon>
        </button>
      </div>
    </mat-card>
  `,
  styles: [
    `
      .cart-item-card {
        margin: 12px 0;
        padding: 16px;
        border-radius: 12px;
        background: white;
        border: 1px solid #e0e0e0;
      }

      .item-content {
        display: flex;
        align-items: center;
        gap: 20px;
        flex-wrap: wrap;
      }

      .product-info {
        flex: 1;
        min-width: 200px;
      }

      .product-name {
        margin: 0;
        font-weight: 600;
        color: #333;
        font-size: 16px;
      }

      .product-price {
        margin: 4px 0 0 0;
        color: #666;
        font-size: 14px;
      }

      .product-stock {
        margin: 6px 0 0 0;
        font-size: 13px;
        font-weight: 500;
        display: flex;
        align-items: center;
        gap: 4px;
      }

      .stock-icon {
        font-size: 16px;
        width: 16px;
        height: 16px;
      }

      .quantity-control {
        display: flex;
        align-items: center;
        gap: 8px;
      }

      .qty-label {
        font-weight: 500;
        font-size: 14px;
        color: #555;
      }

      .qty-input {
        width: 60px;
        padding: 6px 8px;
        border: 1px solid #ccc;
        border-radius: 4px;
        text-align: center;
        font-size: 14px;
      }

      .item-total {
        text-align: right;
        min-width: 100px;
      }

      .total-label {
        margin: 0;
        color: #666;
        font-size: 12px;
        font-weight: 500;
      }

      .total-value {
        margin: 4px 0 0 0;
        font-weight: 600;
        color: #1976d2;
        font-size: 16px;
      }

      .remove-btn {
        margin-left: auto;
      }

      @media (max-width: 640px) {
        .item-content {
          flex-direction: column;
          align-items: flex-start;
        }

        .item-total,
        .remove-btn {
          align-self: flex-end;
          margin-left: 0;
        }
      }
    `,
  ],
})
export class CartItemComponent {
  @Input() item!: CartItem;
  @Output() quantityChanged = new EventEmitter<number>();
  @Output() removed = new EventEmitter<{ productId: number }>();

  quantity: number = 0;

  ngOnInit() {
    this.quantity = this.item.quantity;
  }

  get itemTotal(): number {
    return this.item.product.price * this.quantity;
  }

  onQuantityChange() {
    if (this.quantity > 0) {
      this.quantityChanged.emit(this.quantity);
    }
  }

  onRemove() {
    this.removed.emit({ productId: this.item.product.id });
  }

  formatPrice(price: number): string {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'EUR',
    }).format(price);
  }
}

