import { Component, Input, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { MatCardModule } from '@angular/material/card';
import { MatButtonModule } from '@angular/material/button';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { MatSelectModule } from '@angular/material/select';
import { MatIconModule } from '@angular/material/icon';
import { FormsModule } from '@angular/forms';
import { CartState } from '../../state/cart/cart.models';
import { Store } from '@ngrx/store';
import * as DiscountsActions from '../../state/discounts/discounts.actions';
import * as DeliveryActions from '../../state/delivery/delivery.actions';
import {
  selectAppliedCoupon,
  selectDiscountAmount,
} from '../../state/discounts/discounts.selectors';
import {
  selectAvailableDeliveryOptions,
  selectSelectedDeliveryOption,
  selectDeliveryOptionCost,
} from '../../state/delivery/delivery.selectors';
import { DeliveryOption } from '../../state/delivery/delivery.actions';

@Component({
  standalone: true,
  selector: 'app-cart-summary',
  imports: [
    CommonModule,
    MatCardModule,
    MatButtonModule,
    MatFormFieldModule,
    MatInputModule,
    MatSelectModule,
    MatIconModule,
    FormsModule,
  ],
  template: `
    <mat-card class="summary-card">
      <h3 class="summary-title">Order Summary</h3>

      <div class="coupon-section">
        <mat-form-field appearance="fill" class="coupon-input">
          <mat-label>Coupon Code</mat-label>
          <input matInput [(ngModel)]="couponCode" placeholder="Enter code" />
          <button mat-icon-button matSuffix (click)="applyCoupon()" [disabled]="!couponCode">
            <mat-icon>check_circle</mat-icon>
          </button>
        </mat-form-field>
        <div class="coupon-hint text-xs text-slate-500 mt-1">
          Try: <strong>SAVE10</strong>, <strong>SAVE15</strong>, <strong>SAVE20</strong>,
          <strong>WELCOME</strong>
        </div>
        <div *ngIf="appliedCoupon$ | async as coupon" class="coupon-applied text-green-600 mt-2">
          ✓ Coupon "{{ coupon.code }}" applied
        </div>
      </div>

      <mat-form-field appearance="fill" class="delivery-select">
        <mat-label>Delivery Option</mat-label>
        <mat-select (selectionChange)="selectDelivery($event.value)">
          <mat-option *ngFor="let option of availableOptions$ | async" [value]="option">
            {{ option.name }} - {{ option.cost | currency: 'EUR' }} ({{ option.estimatedDays }}
            days)
          </mat-option>
        </mat-select>
      </mat-form-field>

      <div class="summary-row">
        <span>Items ({{ cart.itemCount }})</span>
        <span>{{ formatPrice(cart.totalPrice) }}</span>
      </div>

      <div *ngIf="discountAmount$ | async as discount" class="summary-row discount">
        <span>Coupon Discount</span>
        <span>-{{ formatPrice(discount) }}</span>
      </div>

      <div *ngIf="deliveryCost$ | async as cost" class="summary-row">
        <span>Delivery</span>
        <span>{{ formatPrice(cost) }}</span>
      </div>

      <div class="summary-row">
        <span>Tax</span>
        <span>{{ formatPrice(taxAmount) }}</span>
      </div>

      <div class="summary-divider"></div>

      <div class="summary-row total">
        <span>Total</span>
        <span>{{ formatPrice(totalAmount) }}</span>
      </div>
    </mat-card>
  `,
  styles: [
    `
      .summary-card {
        padding: 20px;
        border-radius: 12px;
        background: white;
        border: 1px solid #e0e0e0;
        box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
      }

      .summary-title {
        margin: 0 0 16px 0;
        font-size: 18px;
        font-weight: 600;
        color: #333;
      }

      .coupon-section {
        margin-bottom: 16px;
      }

      .coupon-input {
        width: 100%;
        margin-bottom: 8px;
      }

      .coupon-applied {
        font-size: 12px;
        font-weight: 500;
        padding: 4px 0;
      }

      .delivery-select {
        width: 100%;
        margin-bottom: 16px;
      }

      .summary-row {
        display: flex;
        justify-content: space-between;
        padding: 8px 0;
        font-size: 14px;
        color: #666;
      }

      .summary-row span:last-child {
        font-weight: 500;
      }

      .summary-row.discount {
        color: #16a34a;
        font-weight: 600;
      }

      .summary-divider {
        height: 1px;
        background: #e0e0e0;
        margin: 12px 0;
      }

      .summary-row.total {
        font-size: 16px;
        font-weight: 600;
        color: #333;
      }

      .summary-row.total span:last-child {
        color: #1976d2;
        font-size: 18px;
      }
    `,
  ],
})
export class CartSummaryComponent implements OnInit {
  @Input() cart!: any;

  couponCode = '';
  appliedCoupon$;
  discountAmount$;
  availableOptions$;
  selectedDelivery$;
  deliveryCost$;

  constructor(private store: Store) {
    this.appliedCoupon$ = this.store.select(selectAppliedCoupon);
    this.discountAmount$ = this.store.select(selectDiscountAmount);
    this.availableOptions$ = this.store.select(selectAvailableDeliveryOptions);
    this.selectedDelivery$ = this.store.select(selectSelectedDeliveryOption);
    this.deliveryCost$ = this.store.select(selectDeliveryOptionCost);
  }

  ngOnInit() {
    const options: DeliveryOption[] = [
      {
        id: 'standard',
        name: 'Standard Delivery',
        cost: 5.99,
        estimatedDays: 5,
        description: 'Delivery in 5-7 business days',
      },
      {
        id: 'express',
        name: 'Express Delivery',
        cost: 12.99,
        estimatedDays: 2,
        description: 'Delivery in 2-3 business days',
      },
      {
        id: 'overnight',
        name: 'Overnight Delivery',
        cost: 24.99,
        estimatedDays: 1,
        description: 'Next business day delivery',
      },
      {
        id: 'pickup',
        name: 'Store Pickup',
        cost: 0,
        estimatedDays: 0,
        description: 'Free pickup at store',
      },
    ];
    this.store.dispatch(DeliveryActions.setDeliveryOptions({ options }));
  }

  applyCoupon() {
    if (this.couponCode.trim()) {
      const discountPercent = this.calculateCouponDiscount();
      if (discountPercent > 0) {
        const coupon = {
          code: this.couponCode,
          discount: discountPercent,
          type: 'percentage' as const,
        };
        const discountAmount = (this.cart.totalPrice * discountPercent) / 100;
        this.store.dispatch(DiscountsActions.applyCouponSuccess({ coupon, discountAmount }));
      } else {
        this.store.dispatch(
          DiscountsActions.applyCouponFailure({
            error: 'Invalid coupon code',
          }),
        );
      }
      this.couponCode = '';
    }
  }

  selectDelivery(option: DeliveryOption) {
    this.store.dispatch(DeliveryActions.selectDeliveryOption({ option }));
  }

  calculateCouponDiscount(): number {
    const codes: { [key: string]: number } = {
      SAVE10: 10,
      SAVE15: 15,
      SAVE20: 20,
      WELCOME: 5,
    };
    return codes[this.couponCode.toUpperCase()] || 0;
  }

  get taxAmount(): number {
    return this.cart.totalPrice * 0.08;
  }

  get totalAmount(): number {
    let total = this.cart.totalPrice + this.taxAmount;
    if (this.selectedDelivery$) {
      this.deliveryCost$.subscribe((cost: number) => {
        total += cost;
      });
    }
    return total;
  }

  formatPrice(price: number): string {
    return new Intl.NumberFormat('en-FR', {
      style: 'currency',
      currency: 'EUR',
    }).format(price);
  }
}

