import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ActivatedRoute, RouterLink } from '@angular/router';
import { Store } from '@ngrx/store';
import { MatCardModule } from '@angular/material/card';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { MatProgressSpinnerModule } from '@angular/material/progress-spinner';
import { ReactiveFormsModule, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Product } from '../../services/types';
import * as CartActions from '../../state/cart/cart.actions';
import { ShopApiService } from '../../services/shop-api.service';
import { finalize } from 'rxjs/operators';

@Component({
  standalone: true,
  selector: 'app-product-details-page',
  imports: [
    CommonModule,
    RouterLink,
    MatCardModule,
    MatButtonModule,
    MatIconModule,
    MatFormFieldModule,
    MatInputModule,
    MatProgressSpinnerModule,
    ReactiveFormsModule,
  ],
  template: `
    <div class="min-h-screen containerbg from-blue-50 via-sky-100 to-indigo-100 px-4 py-10">
      <div class="mx-auto flex max-w-4xl flex-col gap-6">
        <div
          class="flex flex-col gap-6 rounded-2xl border border-white/70 bg-white/80 p-6 shadow-xl backdrop-blur-md"
        >
          <div class="flex flex-col gap-4 sm:flex-row sm:items-center sm:justify-between">
            <div>
              <p class="text-xs font-semibold uppercase tracking-[0.16em] text-sky-600">
                Product Details
              </p>
              <h1 class="mt-2 text-3xl font-semibold text-slate-900">{{ product?.name }}</h1>
            </div>

            <button
              mat-stroked-button
              color="primary"
              routerLink="/shop/products"
              class="!border-sky-500 !bg-white !text-sky-700 shadow-sm hover:!bg-sky-50"
            >
              ← Back to Products
            </button>
          </div>
        </div>

        <div
          *ngIf="product"
          class="rounded-2xl border border-white/70 bg-white/80 p-6 shadow-xl backdrop-blur-md"
        >
          <div class="grid gap-8 md:grid-cols-2">
            <div
              class="flex items-center justify-center bg-slate-50 rounded-lg p-8 overflow-hidden"
            >
              <img
                *ngIf="product?.image"
                [src]="product.image"
                [alt]="product.name"
                class="h-full w-full object-cover rounded-lg"
              />
              <div *ngIf="!product?.image" class="text-center">
                <mat-icon class="product-icon">image</mat-icon>
                <p class="text-slate-500 mt-4">Product Image</p>
              </div>
            </div>

            <div class="space-y-6">
              <div>
                <h2 class="text-2xl font-bold text-slate-900">{{ product.name }}</h2>
                <div class="flex items-center gap-4 mt-2">
                  <span class="text-3xl font-bold text-sky-600">{{
                    product.price | currency: 'EUR'
                  }}</span>
                  <span class="text-sm text-slate-600">Product ID: {{ product.id }}</span>
                </div>
              </div>

              <div>
                <h3 class="font-semibold text-slate-900 mb-2">Rating</h3>
                <div class="flex items-center gap-2">
                  <span class="text-yellow-500 text-lg">★★★★★</span>
                  <span class="text-slate-600">{{ product.avgRating }}/5</span>
                </div>
              </div>

              <div class="flex items-center gap-6">
                <div>
                  <h3 class="font-semibold text-slate-900 mb-2">Availability</h3>
                  <div
                    class="flex items-center gap-2"
                    [ngClass]="{
                      'text-green-600': product.stock > 20,
                      'text-yellow-600': product.stock > 5 && product.stock <= 20,
                      'text-red-600': product.stock <= 5,
                    }"
                  >
                    <mat-icon>{{ product.stock > 0 ? 'check_circle' : 'error_circle' }}</mat-icon>
                    <span class="font-semibold">{{ product.stock }} in stock</span>
                  </div>
                </div>
                <div *ngIf="product.discount" class="bg-red-100 rounded-lg px-4 py-2">
                  <p class="text-red-600 font-bold text-lg">-{{ product.discount }}%</p>
                  <p class="text-red-600 text-xs">Discount</p>
                </div>
              </div>

              <div>
                <h3 class="font-semibold text-slate-900 mb-2">Description</h3>
                <p class="text-slate-600 leading-relaxed">
                  This is a high-quality product that meets all your needs. Perfect for everyday use
                  with excellent durability and reliability.
                </p>
              </div>

              <form [formGroup]="addToCartForm" (ngSubmit)="addToCart()" class="space-y-4">
                <mat-form-field appearance="outline" class="w-full">
                  <mat-label>Quantity</mat-label>
                  <input matInput type="number" min="1" max="100" formControlName="quantity" />
                </mat-form-field>

                <button
                  mat-raised-button
                  color="primary"
                  type="submit"
                  class="w-full h-12"
                  [disabled]="addToCartForm.invalid"
                >
                  <mat-icon>shopping_cart</mat-icon>
                  Add to Cart
                </button>
              </form>

              <button mat-stroked-button routerLink="/shop/cart" color="primary" class="w-full">
                View Cart
              </button>
            </div>
          </div>
        </div>

        <!-- Loading State -->
        <div *ngIf="!product" class="flex justify-center py-12">
          <mat-spinner diameter="40"></mat-spinner>
        </div>
      </div>
    </div>
  `,
  styles: [
    `
      .containerbg {
        background: linear-gradient(135deg, #f0f9ff 0%, #e0f2fe 50%, #e0e7ff 100%);
      }

      .product-icon {
        font-size: 64px;
        width: 64px;
        height: 64px;
        color: #ccc;
      }
    `,
  ],
})
export class ProductDetailsPageComponent implements OnInit {
  product: Product | null = null;
  isLoading = true;
  addToCartForm: FormGroup;

  constructor(
    private route: ActivatedRoute,
    private store: Store,
    private fb: FormBuilder,
    private api: ShopApiService,
  ) {
    this.addToCartForm = this.fb.group({
      quantity: [1, [Validators.required, Validators.min(1), Validators.max(100)]],
    });
  }

  ngOnInit() {
    this.route.params.subscribe((params) => {
      const productId = +params['id'];
      this.loadProductDetails(productId);
    });
  }

  private loadProductDetails(productId: number) {
    this.isLoading = true;
    this.api
      .getProduct(productId)
      .pipe(
        finalize(() => {
          this.isLoading = false;
        }),
      )
      .subscribe({
        next: (product) => {
          this.product = product;
        },
        error: (error) => {
          console.error('Failed to load product:', error);
        },
      });
  }

  addToCart() {
    if (this.product && this.addToCartForm.valid) {
      const quantity = this.addToCartForm.get('quantity')?.value || 1;
      this.store.dispatch(CartActions.addToCart({ product: this.product, quantity }));
      alert('Product added to cart!');
      this.addToCartForm.reset({ quantity: 1 });
    }
  }

  formatPrice(price: number): string {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'EUR',
    }).format(price);
  }
}

