import { Component, OnInit, OnDestroy } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterLink } from '@angular/router';
import { ReactiveFormsModule, FormBuilder, FormGroup } from '@angular/forms';
import { MatPaginatorModule } from '@angular/material/paginator';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { MatSelectModule } from '@angular/material/select';
import { MatButtonModule } from '@angular/material/button';
import { MatCardModule } from '@angular/material/card';
import { MatProgressSpinnerModule } from '@angular/material/progress-spinner';
import { MatIconModule } from '@angular/material/icon';
import { Store } from '@ngrx/store';
import * as ProductsActions from '../state/products/products.actions';
import {
  selectAllProducts,
  selectProductsLoading,
  selectProductsError,
  selectProductsCount,
} from '../state/products/products.selectors';
import * as WishlistActions from '../state/wishlist/wishlist.actions';
import { selectIsInWishlist, selectWishlistItems } from '../state/wishlist/wishlist.selectors';
import { Observable, Subject } from 'rxjs';
import { takeUntil } from 'rxjs/operators';

export interface Product {
  id: number;
  name: string;
  price: number;
  created_at: string;
  image: string;
  avgRating: number;
  stock: number;
  discount?: number;
}

@Component({
  standalone: true,
  selector: 'app-products-page',
  imports: [
    CommonModule,
    RouterLink,
    ReactiveFormsModule,
    MatPaginatorModule,
    MatFormFieldModule,
    MatInputModule,
    MatSelectModule,
    MatButtonModule,
    MatCardModule,
    MatProgressSpinnerModule,
    MatIconModule,
  ],
  template: `
    <div class="min-h-screen containerbg from-blue-50 via-sky-100 to-indigo-100 px-4 py-10">
      <div class="mx-auto flex max-w-6xl flex-col gap-6">
        <div
          class="flex flex-col gap-6 rounded-2xl border border-white/70 bg-white/80 p-6 shadow-xl backdrop-blur-md"
        >
          <div class="flex flex-col gap-4 sm:flex-row sm:items-center sm:justify-between">
            <div>
              <p class="text-xs font-semibold uppercase tracking-[0.16em] text-sky-600">
                Catalogue
              </p>
              <h1 class="mt-2 text-3xl font-semibold text-slate-900">Shop Products</h1>
              <p class="mt-1 text-sm text-slate-600">
                Fine-tune your view by page, rating and ordering.
              </p>
            </div>

            <button
              mat-stroked-button
              color="primary"
              routerLink="/app"
              class="!border-sky-500 !bg-white !text-sky-700 shadow-sm hover:!bg-sky-50"
            >
              ← Back to dashboard
            </button>
          </div>

          <div class="p-4">
            <form
              [formGroup]="filterForm"
              (ngSubmit)="applyFilters()"
              class="mt-4 grid gap-3 md:grid-cols-3"
            >
              <mat-form-field appearance="fill" class="w-full text-sm">
                <mat-label>Items per Page</mat-label>
                <mat-select formControlName="pageSize">
                  <mat-option value="6">6 items</mat-option>
                  <mat-option value="12">12 items</mat-option>
                  <mat-option value="24">24 items</mat-option>
                  <mat-option value="48">48 items</mat-option>
                </mat-select>
              </mat-form-field>

              <mat-form-field appearance="fill" class="w-full text-sm">
                <mat-label>Min Rating</mat-label>
                <input
                  matInput
                  type="text"
                  inputmode="decimal"
                  formControlName="minRating"
                  class="text-sm"
                />
              </mat-form-field>

              <div class="flex flex-col gap-3 md:flex-row md:items-end">
                <mat-form-field appearance="fill" class="w-full text-sm">
                  <mat-label>Sort By</mat-label>
                  <mat-select formControlName="ordering">
                    <mat-option value="">Default</mat-option>
                    <mat-option value="price">Price (Low to High)</mat-option>
                    <mat-option value="-price">Price (High to Low)</mat-option>
                    <mat-option value="name">Name (A–Z)</mat-option>
                  </mat-select>
                </mat-form-field>

                <button
                  mat-raised-button
                  color="primary"
                  type="submit"
                  class="h-11 w-full md:w-auto"
                >
                  Apply
                </button>
              </div>
            </form>
          </div>
        </div>

        <div *ngIf="loading$ | async" class="flex justify-center py-10">
          <mat-spinner diameter="40"></mat-spinner>
        </div>

        <div
          *ngIf="error$ | async as error"
          class="rounded-xl border border-red-200 bg-red-50/80 px-4 py-3 text-sm text-red-700 shadow-sm"
        >
          {{ error }}
        </div>

        <div *ngIf="products$ | async as products" class="space-y-4">
          <div
            class="grid gap-6 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4"
            *ngIf="(products?.length ?? 0) > 0"
          >
            <div
              *ngFor="let product of products"
              class="group relative overflow-hidden rounded-2xl border border-slate-200/50 bg-white transition-all duration-300 hover:border-sky-300 hover:shadow-xl hover:-translate-y-1"
            >
              <div
                class="relative h-48 overflow-hidden bg-gradient-to-br from-slate-100 to-slate-200"
              >
                <img
                  [src]="product.image"
                  [alt]="product.name"
                  class="h-full w-full object-cover transition-transform duration-300 group-hover:scale-110"
                  loading="lazy"
                />
                <div
                  class="absolute inset-0 bg-gradient-to-t from-black/20 to-transparent opacity-0 transition-opacity group-hover:opacity-100"
                ></div>
                <div class="absolute top-2 right-2 flex flex-col gap-2">
                  <div
                    *ngIf="product.discount"
                    class="inline-flex items-center rounded-lg bg-red-500 px-2 py-1 text-xs font-bold text-white shadow-md"
                  >
                    -{{ product.discount }}%
                  </div>
                  <div
                    [ngClass]="{
                      'bg-green-500': product.stock > 20,
                      'bg-yellow-500': product.stock > 5 && product.stock <= 20,
                      'bg-red-500': product.stock <= 5,
                    }"
                    class="inline-flex items-center rounded-lg px-2 py-1 text-xs font-bold text-white shadow-md"
                  >
                    {{ product.stock }} in stock
                  </div>
                </div>
              </div>

              <div class="flex flex-col p-4">
                <div class="mb-3 flex items-start justify-between gap-2">
                  <span
                    class="inline-flex items-center rounded-lg bg-sky-100 px-3 py-1 text-sm font-bold text-sky-700"
                  >
                    {{ product.price | currency: 'EUR' }}
                  </span>
                  <span class="text-xs font-medium text-slate-500"> ID: {{ product.id }} </span>
                </div>

                <h2
                  class="mb-2 line-clamp-2 text-sm font-bold text-slate-900 group-hover:text-sky-600"
                >
                  {{ product.name }}
                </h2>

                <div class="mb-4 flex items-center gap-2">
                  <div class="flex items-center">
                    <span class="text-amber-400">★</span>
                    <span class="ml-1 text-xs font-bold text-slate-800">
                      {{ product.avgRating | number: '1.1-1' }}
                    </span>
                  </div>
                  <span class="text-xs text-slate-500">/ 5</span>
                  <span class="ml-auto text-xs text-slate-400">
                    {{ product.created_at | date: 'short' }}
                  </span>
                </div>

                <div class="mt-auto flex gap-2">
                  <button
                    mat-raised-button
                    color="primary"
                    [routerLink]="['/shop/products', product.id]"
                    class="flex-1 !rounded-lg !bg-gradient-to-r !from-sky-500 !to-blue-600 !text-white text-xs font-semibold h-9"
                  >
                    View
                  </button>
                  <button
                    mat-icon-button
                    [class.!text-red-500]="selectIsInWishlist(product.id) | async"
                    [class.!text-slate-400]="!(selectIsInWishlist(product.id) | async)"
                    (click)="toggleWishlist(product)"
                    class="transition hover:!text-red-500"
                    [title]="
                      (selectIsInWishlist(product.id) | async)
                        ? 'Remove from wishlist'
                        : 'Add to wishlist'
                    "
                  >
                    <mat-icon>{{
                      (selectIsInWishlist(product.id) | async) ? 'favorite' : 'favorite_border'
                    }}</mat-icon>
                  </button>
                </div>
              </div>
            </div>
          </div>

          <div
            *ngIf="(products?.length ?? 0) === 0"
            class="flex flex-col items-center justify-center rounded-2xl border border-dashed border-slate-300 bg-white/80 px-6 py-10 text-center text-slate-500"
          >
            <div
              class="mb-3 flex h-10 w-10 items-center justify-center rounded-full bg-sky-50 text-sky-500"
            >
              <mat-icon>shopping_cart</mat-icon>
            </div>
            <p class="text-sm font-medium">No products found</p>
            <p class="mt-1 text-xs text-slate-400">
              Try adjusting your filters or reset the page &amp; rating.
            </p>
          </div>

          <div *ngIf="(products?.length ?? 0) > 0" class="flex justify-center py-4">
            <mat-paginator
              [length]="totalProducts"
              [pageSize]="pageSize"
              [pageSizeOptions]="[6, 12, 24, 48]"
              (page)="onPageChange($event)"
              showFirstLastButtons
            ></mat-paginator>
          </div>
        </div>
      </div>
    </div>
  `,
  styles: [
    `
      :host {
        font-family:
          system-ui,
          -apple-system,
          BlinkMacSystemFont,
          'Segoe UI',
          sans-serif;
        color: #0f172a;
      }

      .containerbg {
        background: radial-gradient(circle at top left, #439cf5 0, #b6dcff 40%, #62adf2 100%);
      }

      .product-card {
        border-radius: 1rem;
        background: #ffffff;
        border: 1px solid #e2e8f0;
        box-shadow: 0 10px 25px rgba(15, 23, 42, 0.06);
        overflow: hidden;
        transition:
          transform 0.18s ease,
          box-shadow 0.18s ease,
          border-color 0.18s ease;
      }

      .product-card:hover {
        transform: translateY(-3px);
        box-shadow: 0 18px 35px rgba(15, 23, 42, 0.12);
        border-color: #bfdbfe;
      }

      .mat-mdc-select-panel,
      .mat-mdc-option,
      .mat-mdc-select-value-text,
      .mat-mdc-form-field,
      .mat-mdc-input-element {
        font-family:
          system-ui,
          -apple-system,
          BlinkMacSystemFont,
          'Segoe UI',
          sans-serif !important;
      }
    `,
  ],
})
export class ProductsPageComponent implements OnInit, OnDestroy {
  filterForm: FormGroup;
  products$: Observable<Product[]>;
  loading$: Observable<boolean>;
  error$: Observable<string | null>;
  totalProducts$: Observable<number>;
  totalProducts = 0;
  pageSize = 6;
  currentPage = 0;
  private destroy$ = new Subject<void>();
  wishlistItems: any[] = [];

  constructor(
    private fb: FormBuilder,
    private store: Store,
  ) {
    this.filterForm = this.fb.group({
      pageSize: [6],
      minRating: [0],
      ordering: [''],
    });

    this.products$ = this.store.select(selectAllProducts);
    this.loading$ = this.store.select(selectProductsLoading);
    this.error$ = this.store.select(selectProductsError);
    this.totalProducts$ = this.store.select(selectProductsCount);
    this.totalProducts$.pipe(takeUntil(this.destroy$)).subscribe((count) => {
      this.totalProducts = count;
    });

    // Subscribe to wishlist items to keep track
    this.store
      .select(selectWishlistItems)
      .pipe(takeUntil(this.destroy$))
      .subscribe((items: any) => {
        this.wishlistItems = items;
      });
  }

  ngOnInit(): void {
    this.applyFilters();
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
  }

  applyFilters(): void {
    this.currentPage = 0;
    this.loadProducts();
  }

  onPageChange(event: any): void {
    this.currentPage = event.pageIndex;
    this.pageSize = event.pageSize;
    this.filterForm.patchValue({ pageSize: event.pageSize });
    this.loadProducts();
  }

  selectIsInWishlist(productId: number): Observable<boolean> {
    return this.store.select(selectIsInWishlist(productId));
  }

  toggleWishlist(product: Product): void {
    const isInWishlist = this.wishlistItems.some((item) => item.id === product.id);

    if (isInWishlist) {
      this.store.dispatch(WishlistActions.removeFromWishlist({ productId: product.id }));
    } else {
      this.store.dispatch(
        WishlistActions.addToWishlist({
          product: {
            id: product.id,
            name: product.name,
            price: product.price,
            image: product.image,
          },
        }),
      );
    }
  }

  private loadProducts(): void {
    const pageSize = Number(this.filterForm.get('pageSize')?.value) || 6;
    const minRating = Number(this.filterForm.get('minRating')?.value) || 0;

    const filters = {
      page: this.currentPage,
      pageSize,
      minRating,
      ordering: this.filterForm.get('ordering')?.value || '',
    };

    this.store.dispatch(ProductsActions.loadProducts({ filters }));
  }
}

