createReducer(
  initialCartState,
  on(CartActions.addToCart, (state, { product, quantity }) => {
    const existingItem = state.items.find((item) => item.product.id === product.id);

    let newItems;
    if (existingItem) {
      newItems = state.items.map((item) =>
        item.product.id === product.id ? { ...item, quantity: item.quantity + quantity } : item,
      );
    } else {
      newItems = [...state.items, { product, quantity }];
    }

    return {
      ...state,
      items: newItems,
      totalPrice: calculateTotalPrice(newItems),
      itemCount: calculateItemCount(newItems),
    };
  }),

  on(CartActions.removeFromCart, (state, { productId }) => {
    const newItems = state.items.filter((item) => item.product.id !== productId);

    return {
      ...state,
      items: newItems,
      totalPrice: calculateTotalPrice(newItems),
      itemCount: calculateItemCount(newItems),
    };
  }),

  on(CartActions.updateCartItemQuantity, (state, { productId, quantity }): CartState => {
    if (quantity <= 0) {
      const newItems = state.items.filter((item) => item.product.id !== productId);
      return {
        ...state,
        items: newItems,
        totalPrice: calculateTotalPrice(newItems),
        itemCount: calculateItemCount(newItems),
      };
    }

    const newItems = state.items.map((item) =>
      item.product.id === productId ? { ...item, quantity } : item,
    );

    return {
      ...state,
      items: newItems,
      totalPrice: calculateTotalPrice(newItems),
      itemCount: calculateItemCount(newItems),
    };
  }),

  on(CartActions.clearCart, () => initialCartState),

  on(CartActions.loadCartFromStorage, (state, { items }) => ({
    ...state,
    items: items || [],
    totalPrice: calculateTotalPrice(items || []),
    itemCount: calculateItemCount(items || []),
  })),
)

createReducer(
  initialState,

  on(DeliveryActions.selectDeliveryOption, (state, { option }) => ({
    ...state,
    selectedOption: option,
  })),

  on(DeliveryActions.setDeliveryOptions, (state, { options }) => ({
    ...state,
    options,
    selectedOption: state.selectedOption || options[0],
  })),
)

createReducer(
  initialState,

  on(DiscountsActions.applyCoupon, (state, { code }) => state),

  on(DiscountsActions.applyCouponSuccess, (state, { coupon, discountAmount }) => ({
    ...state,
    appliedCoupon: coupon,
    discountAmount,
    error: null,
  })),

  on(DiscountsActions.applyCouponFailure, (state, { error }) => ({
    ...state,
    error,
    appliedCoupon: null,
    discountAmount: 0,
  })),

  on(DiscountsActions.removeCoupon, (state) => ({
    ...state,
    appliedCoupon: null,
    discountAmount: 0,
    error: null,
  })),

  on(DiscountsActions.addProductDiscount, (state, { productId, discount }) => ({
    ...state,
    productDiscounts: {
      ...state.productDiscounts,
      [productId]: discount,
    },
  })),

  on(DiscountsActions.removeProductDiscount, (state, { productId }) => {
    const newProductDiscounts = { ...state.productDiscounts };
    delete newProductDiscounts[productId];
    return {
      ...state,
      productDiscounts: newProductDiscounts,
    };
  }),
)

